import React, { useState }from 'react';

const SearchBar = ({ onFormSubmit }) => {
  const [term, setTerm] = useState('');
  const [selectedOption, setSelectedOption] = useState('');

  const onSubmit = (event) => {
    event.preventDefault();

    onFormSubmit(term, selectedOption);
  };

  const onValueChange = ( option ) =>{
    setSelectedOption(option);
  };


  return (
      <div className="search-bar ui segment">
        <form onSubmit={onSubmit} className="ui form">
          <div className="field">
            <label>Search for Lego Set</label>
            <input
              type="text"
              value={term}
              onChange={(event) => setTerm(event.target.value)}
            />
          </div>
          <div className="radiobuttongroup ui grid"> {/*start of radio buttons Option*/}
            <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="netflix"
                checked={selectedOption === "netflix"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Netflix
            </label>
          </div>
          <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="prime"
                checked={selectedOption === "prime"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Prime
            </label>
          </div>
          <div className="radioone two wide column">
            <label>
              <input
                type="radio"
                value="hulu"
                checked={selectedOption === "hulu"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Hulu
            </label>
          </div>
          <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="disney"
                checked={selectedOption === "disney"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Disney+
            </label>
          </div>
          <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="hbo"
                checked={selectedOption === "hbo"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;HBO
            </label>
          </div>
          <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="peacock"
                checked={selectedOption === "peacock"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Peacock
            </label>
          </div>
           <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="paramount"
                checked={selectedOption === "paramount"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Paramount+
            </label>
          </div>
           <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="starz"
                checked={selectedOption === "starz"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Starz
            </label>
          </div>
           <div className="radio two wide column">
            <label>
              <input
                type="radio"
                value="showtime"
                checked={selectedOption === "showtime"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Showtime
            </label>
          </div>
        </div> {/*end of options*/}
        <button className="btn btn-default" type="submit">
          Submit
        </button>
        </form>
      </div>
    );

};



export default SearchBar;
