import React, { useState }from 'react';

const SearchBar = ({ onFormSubmit }) => {
  const [term, setTerm] = useState('');
  const [selectedOption, setSelectedOption] = useState('netflix');
  const [selectedType, setSelectedType] = useState('series');
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  const onSubmit = (event) => {
    event.preventDefault();

    onFormSubmit(term, selectedOption, selectedType, selectedLanguage);
  };

  const onValueChange = ( option ) =>{
    setSelectedOption(option);
  };


  return (
      <div className="search-bar ui segment">
        <form onSubmit={onSubmit} className="ui form">
          <div className="field">
            <label>Search</label>
            <input
              type="text"
              value={term}
              onChange={(event) => setTerm(event.target.value)}
            />
          </div>
          <div className="radiobuttongroup"> {/*start of radio buttons Option*/}
            <div className="radio">
            <label>
              <input
                type="radio"
                value="netflix"
                checked={selectedOption === "netflix"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Netflix
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="prime"
                checked={selectedOption === "prime"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Prime
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="hulu"
                checked={selectedOption === "hulu"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Hulu
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="disney"
                checked={selectedOption === "disney"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Disney+
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="hbo"
                checked={selectedOption === "hbo"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              HBO
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="peacock"
                checked={selectedOption === "peacock"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Peacock
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="hbo"
                checked={selectedOption === "hbo"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              HBO
            </label>
          </div>
           <div className="radio">
            <label>
              <input
                type="radio"
                value="paramount"
                checked={selectedOption === "paramount"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Paramount+
            </label>
          </div>
           <div className="radio">
            <label>
              <input
                type="radio"
                value="starz"
                checked={selectedOption === "starz"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Starz
            </label>
          </div>
           <div className="radio">
            <label>
              <input
                type="radio"
                value="showtime"
                checked={selectedOption === "showtime"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              Showtime
            </label>
          </div>
          <div>
            Selected option is : {selectedOption}
          </div>
        </div> {/*end of options*/}
        <div className="radiobuttongroup"> {/*start of type*/}
            <div className="radio">
            <label>
              <input
                type="radio"
                value="movie"
                checked={selectedType === "movie"}
                onChange={(event) => setSelectedType(event.target.value)}
              />
              Movie
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="series"
                checked={selectedType === "series"}
                onChange={(event) => setSelectedType(event.target.value)}
              />
              Series
            </label>
          </div>
          <div>
            Selected type is : {selectedType}
          </div>
        </div> {/*end of type*/}
        <div> {/*start of language options*/}
          <div className="radio">
            <label>
              <input
                type="radio"
                value="en"
                checked={selectedType === "en"}
                onChange={(event) => setSelectedLanguage(event.target.value)}
              />
              English
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="fr"
                checked={selectedType === "fr"}
                onChange={(event) => setSelectedLanguage(event.target.value)}
              />
              French
            </label>
          </div>
        </div> {/*end of language options*/}
        <button className="btn btn-default" type="submit">
          Submit
        </button>
        </form>
      </div>
    );

};

// disney hbo hulu peacock paramount starz showtime


export default SearchBar;
