import React, { useState }from 'react';

const SearchBar = ({ onFormSubmit }) => {
  const [term, setTerm] = useState('');
  const [selectedOption, setSelectedOption] = useState('netflix')

  const onSubmit = (event) => {
    event.preventDefault();

    onFormSubmit(term, selectedOption);
  };

  const onValueChange = ( option ) =>{
    setSelectedOption(option);
  };


  return (
      <div className="search-bar ui segment">
        <form onSubmit={onSubmit} className="ui form">
          <div className="field">
            <label>Netflix Series Search</label>
            <input
              type="text"
              value={term}
              onChange={(event) => setTerm(event.target.value)}
            />
          </div>
          <div className="radio">
          <label>
            <input
              type="radio"
              value="netflix"
              checked={selectedOption === "netflix"}
              onChange={(event) => setSelectedOption(event.target.value)}
            />
            Netflix
          </label>
        </div>
        <div className="radio">
          <label>
            <input
              type="radio"
              value="prime"
              checked={selectedOption === "prime"}
              onChange={(event) => setSelectedOption(event.target.value)}
            />
            Prime
          </label>
        </div>
        <div className="radio">
          <label>
            <input
              type="radio"
              value="hulu"
              checked={selectedOption === "hulu"}
              onChange={(event) => setSelectedOption(event.target.value)}
            />
            Hulu
          </label>
        </div>
        <div>
          Selected option is : {selectedOption}
        </div>
        <button className="btn btn-default" type="submit">
          Submit
        </button>
        </form>
      </div>
    );

};




export default SearchBar;
